import json


# ----------------------------------------------------------------------------------------------------------------------
# User defined functions
# ----------------------------------------------------------------------------------------------------------------------
def read_json(file_path: str):
    """Read JSON file from given file path."""
    with open(file_path) as f:
        data = json.load(f)
    return data
